import React, { useCallback } from 'react';
import ToDoListItem from './ToDoListItem';
import './src/css/TodoList.scss';
import {List} from 'react-virtualized'


function TodoList({ todos, onRemove, onToggle, onChangeSelectedTodo, onInsertToggle }) {
  const rowRender = useCallback(
    ({index,key,style}) => {
      const todo = todos[index];
      return(
        <ToDoListItem
        todo={todo}
        key={key}
        onToggle={onToggle}
        onRemove={onRemove}
        onInsertToggle={onInsertToggle}
        onChangeSelectedTodo={onChangeSelectedTodo}
        style={style}
      />
      )
    },
    [ todos, onRemove, onToggle, onChangeSelectedTodo, onInsertToggle ]
  )

  return (
    <List 
      className='TodoList'
      width={512} // 전체너비
      height={513}// 전체 높이
      rowCount={todos.length}//항목갯수
      rowHeight={57} // 항목 높이
      rowRenderer={rowRender} //항목을 렌더링할 때 쓰는 함수
      list={todos}//배열
      style={{outline:'none'}} //List에 기본 적용되는 outline 스타일 제거
    />
  );
}

export default React.memo(TodoList);
